import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation, useParams } from 'react-router-dom';

function AddOrUpDate() {
    // const navigate = useNavigate();
    // const location = useLocation();
    // const [product, setProduct] = useState({});
    // const [categories, setCategories] = useState([]);

    // useEffect(() => {
    //     setProduct(location.state);
    //     getCategories();
    // }, []);

    // const url = useParams();
    // const act = url.act == 'update' ? 'Update' : 'Add';
const act = 'add';
    // const getCategories = async () => {
    //     const data = await fetch('http://localhost:3090/categories');
    //     const categories = await data.json();
    //     console.log(categories);
    //     setCategories([...categories]);
    // };

    const setInputs = (event) => {
        const temp = client;
        temp[event.target.id] = event.target.value;
        console.log(temp);
        setProduct(temp);
    }

    // const click = async () => {
    //     const data = await fetch(`http://localhost:3090/products/${url.act}`, {
    //         method: 'POST',
    //         headers: {
    //             'Accept': 'application/json',
    //             'Content-Type': 'application/json'
    //         },
    //         body: JSON.stringify({ product: product })
    //     });
    //     navigate(-1);
    // }

    return (
        <div className='container'>
            <p>wellcome to add client!!!</p>
            {/* <h3 onClick={() => { navigate(-1); }}>X</h3> */}
            <div>
                <h1 className='form_title'>{act} Client</h1>
                <div className='form_input-group'>
                    <input type='text' className='form_input' id='name' onBlur={setInputs} placeholder={product.name} />
                </div>
                <div className='form_input-group'>
                    <select defaultValue={'DEFAULT'} id='category' onBlur={setInputs}>
                        <option value='DEFAULT' disabled>{product.category}</option>
                        {categories.map(category => {
                            return (<option key={category.id} value={category.id}>{category.name}</option>)
                        })}
                    </select>
                </div>
                <div className='form_input-group'>
                    <input type='number' className='form_input' id='product_price' onBlur={setInputs} placeholder={product.product_price} />
                </div>
                <div className='form_input-group'>
                    <input type='text' className='form_input' id='location' onBlur={setInputs} placeholder={product.location} />
                </div>
                <button className='form_button' type='submit' onClick={click}>{act}</button>
            </div>
        </div>
    )
}

export default AddOrUpDate;
