import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation, useParams } from 'react-router-dom';

function Add() {
    // const navigate = useNavigate();
    // const location = useLocation();
    const [client, setClient] = useState({});
    // const [categories, setCategories] = useState([]);

    // useEffect(() => {
    //     setProduct(location.state);
    //     getCategories();
    // }, []);

    // const url = useParams();
    // const act = url.act == 'update' ? 'Update' : 'Add';
const act = 'add';
    // const getCategories = async () => {
    //     const data = await fetch('http://localhost:3090/categories');
    //     const categories = await data.json();
    //     console.log(categories);
    //     setCategories([...categories]);
    // };

    const setInputs = (event) => {
        const temp = client;
        temp[event.target.id] = event.target.value;
        console.log(temp);
        setClient(temp);
    }

    const click = async () => {
        
        await fetch(`http://localhost:3090/clients/add`, {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ client: client })
        });
        // navigate(-1);
    }

    return (
        <div className='container'>
            {/* <h3 onClick={() => { navigate(-1); }}>X</h3> */}
            <div>
                <h1 className='form_title'>{act} Client</h1>
                <div className='form_input-group'>
                    <input type='text' className='form_input' id='firstName' onBlur={setInputs} placeholder='First Name'/*{client.firstName}*/ />
                </div>
                <div className='form_input-group'>
                    <input type='text' className='form_input' id='lastName' onBlur={setInputs} placeholder='Last Name'/*{client.firstName}*/ />
                </div>
                <div className='form_input-group'>
                    <input type='text' className='form_input' id='id' onBlur={setInputs} placeholder='Id'/*{client.firstName}*/ />
                </div>
                <div className='form_input-group'>
                    <span>Date Of Birth </span>
                    <input type='date' className='form_input' id='dateOfBirth' onBlur={setInputs} placeholder='Date Of Birth'/*{client.firstName}*/ />
                </div>
                <div className='form_input-group'>
                    <input type='text' className='form_input' id='telephone' onBlur={setInputs} placeholder='Telephone'/*{client.firstName}*/ />
                </div>
                <div className='form_input-group'>
                    <input type='text' className='form_input' id='phone' onBlur={setInputs} placeholder='Phone'/*{client.firstName}*/ />
                </div>
                <div className='form_input-group'>
                    <input type='text' className='form_input' id='telephone' onBlur={setInputs} placeholder='Telephone'/*{client.firstName}*/ />
                </div>

                {/* <div className='form_input-group'>
                    <select defaultValue={'DEFAULT'} id='category' onBlur={setInputs}>
                        <option value='DEFAULT' disabled>{product.category}</option>
                        {categories.map(category => {
                            return (<option key={category.id} value={category.id}>{category.name}</option>)
                        })}
                    </select>
                </div> */}

                <button className='form_button' type='submit' onClick={click}>{act}</button>
            </div>
        </div>
    )
}

export default Add;
