        const data = await fetch(`http://localhost:3090/clients/:update`, {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ client: client })
        });
        navigate(-1);
    }

    return ( <
        div className = 'container' >
