import React, { useState, useEffect } from 'react';
// import { useParams } from 'react-router-dom';
import { Link, useNavigate, useLocation, Outlet } from 'react-router-dom';

function Client() {
    // console.log(`props: ${props}`);
    // console.log(`props.Client: ${props.Client}`);
    
    const navigate = useNavigate();
    const location = useLocation();

    const [client, setClient] = useState([]);
    // // const [basket, setBasket] = useState([]);

    useEffect(() => {
        setClient(location.state);
    }, []);

    const update = (client) => {
        console.log(client);
        navigate('update', { state: client });
    };

    const remove = async (clientId) => {
        console.log(clientId);
        // const data = 
        await fetch('http://localhost:3090/clients/remove', {
            method: 'DELETE',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ client: clientId })
        });
        const newClients = clients.filter(client => client.id !== clientId);
        setClients([...newClients]);
    };

    return (
        <div>
            <h3>{client.firstName} {client.lastName}</h3>
            <p>id: {client.clientId}</p>
            <p>Address: {client.address}</p>
            <p>Date Of Birth: {client.dateOfBirth}</p>
            <p>Telephone: {client.telphone}</p>
            <p>Phone: {client.phone}</p>
            <p>Vaccins Date: {client.vaccinsDate}</p>
            <p>Vaccins Producer: {client.vaccineManufacturer}</p>
            <p>Positive Result: {client.positiveResult}</p>
            <p>Recovery Date: {client.recoveryDate}</p>

            <button onClick={() => { update(client) }}>update</button>
            <button onClick={() => { remove(client.id) }}>remove</button>

            
            {/* <Link to={`/osher-ad.co.il/market/${client.clientId}/${client.id}`} key={client.id}>
                {client.firstName} {client.lastName}

                <button onClick={addToBasket}>+</button>
            </Link> */}
        </div>
    )

    // return (
    //     <div className='container'>
    //         <h3 onClick={() => { navigate(-1); }}>X</h3>
    //         <div>
    //             {/* <h1 className='form_title'>{act} Client</h1> */}
    //             <div className='form_input-group'>
                    
    //                 <input type='text' className='form_input' id='firstName'  placeholder={client.firstName} />
    //             </div>
    //         </div>
    //     </div>
    // )

}

export default Client;
